mapper.update:
update users_info
set form_data = #{formData,jdbcType=VARCHAR},
  login_key = #{loginKey,jdbcType=VARCHAR},
  send_key = #{sendKey,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, form_data, login_key, send_key
from users_info
where id = #{id,jdbcType=INTEGER}
